$(document).ready(function(){
    $(document).on('click','.ctaAnalytics',function(){
        let ctaUrl = $(this).attr("href");
        let ctaText = $(this).text().trim();
        
        dataLayer.push({
            'event':'carousel',
            'eventCategory':'carousel',
            'eventAction':ctaText,
            'eventLabel':ctaUrl
        });
    });
});