$(function() {

  $(document).on('click','.flyoutmenuLinks',function(){
    let ctaText = $(this).children("a").text().trim();
    let nextUrl = $(this).children("a").attr("href");
     if(nextUrl == undefined){
      nextUrl = "";
     }
     let sectionClicked = $(this).parent("ul").parent("div").attr("class");
     if(sectionClicked == undefined){
      sectionClicked = "";
     }
     dataLayer.push({
      'event':'flyoutMenuLanding',
      'eventCategory':'flyoutMenuLanding',
      'eventAction':ctaText,
      'eventLabel':nextUrl,
      'sectionClicked':sectionClicked,
      'imgAltText':""
  });
  });  

  $(document).on('click','.teaserCardAna',function(){
    let ctaText = $(this).children("a").children(".product-image-caption").children(".product-image-txt").children(".teaserBtnAna").text().trim();
    let nextUrl = $(this).children("a").attr("href");
     if(nextUrl == undefined){
      nextUrl = "";
     }
     let sectionClicked = $(this).parent("div").parent(".row").parent(".dropdown-list").attr("id");
     if(sectionClicked == undefined){
      sectionClicked = "";
     }
     let imageAltText =  $(this).children("a").children("img").attr("alt");

     dataLayer.push({
      'event':'flyoutMenuLanding',
      'eventCategory':'flyoutMenuLanding',
      'eventAction':ctaText,
      'eventLabel':nextUrl,
      'sectionClicked':sectionClicked,
      'imgAltText':imageAltText
  });
  });  


  });