$(function() {
  $(document).on('click','#miniCraouselAna .slick-list .slick-track .slick-slide',function(){
      let imageAltText = $(this).children("div").children(".c-item").children("a").children("img").attr("alt");
      let nextUrl = $(this).children("div").children(".c-item").children("a").attr("href");
      let headingName = $(this).children("div").children(".c-item").children("a").children("div").children(".textHeading").text().trim();
      if(nextUrl == undefined){
          nextUrl = "";
      }
        
        dataLayer.push({
          'event':'miniCarousel',
          'eventCategory':'miniCarousel',
          'eventAction':imageAltText,
          'eventLabel':nextUrl,
          'headingName':headingName
       });

  });    

});