$(document).ready(function () {
    $(document).on("change", ".dropdownAna", function () {
        let placeholderText = $(this).attr("data-placeholder");
        let valueAna = $(this).val();
        dataLayer.push({
            'event': 'dropdown',
            'eventCategory': 'dropdown',
            'eventAction': placeholderText,
            'eventLabel': valueAna
        });
    });

    $("select").on("change", function () {
        var conditionalDisplayIDs = this.options[this.selectedIndex].getAttribute(
            "data-conditional-display"
        );
        $(".inView").each(function () {
            $(this).addClass("d-none");
            $(".inView input[type=text]").val("");
        });
        if (conditionalDisplayIDs != null && typeof conditionalDisplayIDs != undefined) {
            var splitArray = conditionalDisplayIDs.split(",");
            for (var i in splitArray) {
                var controlId = ".wrapper-" + splitArray[i];
                $(controlId).removeClass("d-none");
                $(controlId).addClass("inView");
            }
        }
    });
});