
    $(function(){

        $(".cards-overlay").each(function(){
            let href =  $(this).children("a").attr("href");
            var content = href.includes("content");

            if(content==true){
             let replaced = href.replace("/content/royal-enfield","");
                $(this).children("a").attr("href", replaced);
            }
         });


    $(document).on("click",".meteorCardAna",function(){
    let ctaText = $(this).text().trim();
    let nextPageURL = $(this).attr("href");
    if(nextPageURL == undefined){
        nextPageURL = "";
    }
    let headingName = $(this).siblings(".card-heading").text().trim();
    if(headingName == undefined){
        headingName = "";
    }
    dataLayer.push({
    'event':'cards',
    'eventCategory':'cards',
    'eventAction':ctaText,
    'eventLabel':nextPageURL,
    'headingName':headingName 
});
});
    });
