$(function(){
$(document).on("click",".galleryMeteorAna .slick-arrow",function(){
    let arrowClick = $(this).attr("aria-label");
    dataLayer.push({
        'event':'gallery',
        'eventCategory':'gallery',
        'eventAction':arrowClick
    })
});

});
