$(function(){
  
  $(document).on('click','.view-less-feature-btn, .view-all-feature-btn',function(){
    let ctatext = $(this).text().trim();
    let nextPageUrl = $(this).attr("href");
    if(nextPageUrl == undefined){
      nextPageUrl =""
    }
    let headingName = $(this).parent(".view-all-features").parent(".re-banner-caption").siblings(".banner-inner-txt").children(".disp-tbl").children(".disp-tbl-cel").children(".container-fluid").children(".txt-box").children("h2").text().trim();
    dataLayer.push({
      'event':'motorcycleFeaturesCarousel',
      'eventCategory':'motorcycleFeaturesCarousel',
      'eventAction':ctatext,
      'eventLabel':nextPageUrl,
      
    });
  });
});

//view all features [START]
$(document).ready(function(){
	$('.view-all-feature-btn').on('click', function(){
        $(".feature-container-hide").parent().parent().parent(".slick-slide").css("width","100%");
		$('.feature-container-hide').slideToggle();
		//$(this).toggleClass('f-open');
		$(this).hide();

		$(this).parent('.view-all-features').hide();

		$('html, body').animate({
			scrollTop: $('.feature-container-hide').offset().top - 0
		}, 500 );
	});
	$('.view-less-feature-btn').on('click', function(){
        $(".feature-container-hide").parent().parent().parent(".slick-slide").css("width","0px");
		$('.feature-container-hide').slideToggle();
		$('.view-all-feature-btn').show();

		$('.view-all-features').show();

		$('html, body').animate({
			scrollTop: $('.feature-container-hide').offset().top - 0
		}, 500 );
	});

	//features mobile
	//if ($(window).width() < 767) {
		$('.carousel-sections-mobile').slick({
			slidesToShow: 'auto',
			slidesToScroll: 1,
			dots: false,
			centerMode: false,
			arrows: false,
		  	focusOnSelect: true,
			touchThreshold: 15,
			responsive: [
			{
			breakpoint: 767,
			settings: {
		        slidesToShow: 1,
		        slidesToScroll: 1,
				centerMode: true,
				centerPadding: '15px'
				}
			}
			]
		});
	//}
    $(".feature-container-hide").parent().parent().parent(".slick-slide").css("width","0px");
});
//view all features [END]
