$(document).ready(function () {
	if ($(window).width() < 767) {
		$('.marquee-carousel').flexslider({
			animation: "slide",
			controlNav: false,
			animationLoop: false,
			slideshow: false,
			itemWidth: 200,
			itemMargin: 0,
			asNavFor: '.marquee-slider'
		});
	}
	else {
		$('.marquee-carousel').flexslider({
			animation: "slide",
			controlNav: false,
			animationLoop: false,
			slideshow: false,
			itemWidth: 300,
			itemMargin: 0,
			asNavFor: '.marquee-slider'
		});
	}

	$('.marquee-slider').flexslider({
		animation: "fade",
		controlNav: false,
		animationLoop: false,
		slideshow: false,
		sync: ".marquee-carousel",
		start: function (slider) {
			$('body').removeClass('loading');
		}
	});
});

//traveling bike active position
$(document).on('touchstart click', '#carousel .thumb-item', function () {
	$('#carousel .thumb-item').removeClass('thumb-active');
	$(this).addClass('thumb-active');

	var position = $(".thumb-active").position();
	$('.thumb-travel').css('left', position.left);

	// $(this).each(function(){
	//   var fd_left = $('.flex-active-slide').offset().left;
	// });
});
$(function(){
    function upcomingCarouselAnalytics(ctaText,nextPageURL,headingName){
        dataLayer.push({
            'event':'upcomingRidesCarousel',
            'eventCategory':'upcomingRidesCarousel',
            'eventAction':ctaText,
            'eventLabel':nextPageURL,
            'headingName':headingName  
        });
    }
    $(document).on('click','.upcomingRideBtnAna',function(){
        let ctaText = $(this).text().trim();
        let nextPageURL = $(this).attr("href");
        let headingName = $(this).siblings("h2").text().trim();
        if(nextPageURL == undefined){
            nextPageURL=""
        }
        upcomingCarouselAnalytics(ctaText,nextPageURL,headingName);
    });

    $(document).on('click','.upcomingRideCarouselAna li',function(){
        let ctaText = $(this).children("h3").text().trim();
        let nextPageURL = $(this).attr("href");
        let headingName = "";
        if(nextPageURL == undefined){
            nextPageURL=""
        }
        upcomingCarouselAnalytics(ctaText,nextPageURL,headingName);
    });
});
