$(document).ready(function () {
	//carousel-grid-four
	$('.trip-stories-carousel').slick({
		slidesToScroll: 1,
		arrows: true,
		fade: false,
		infinite: true,
		speed: 1000,
	  	touchThreshold: 15,
		variableWidth: true,
		responsive: [
		{
		breakpoint: 1080,
		settings: {
	        slidesToScroll: 1,
			speed: 200,
		  	centerMode: true,
		  	centerPadding: '15px',
		  	focusOnSelect: true,
		  	infinite: true,
		  	dots: false,
  			touchThreshold: 15,
			}
		},
		{
		breakpoint: 600,
		settings: {
	        slidesToScroll: 1,
	        slidesToShow: 1,
		  	centerMode: true,
		  	centerPadding: '15px',
		  	focusOnSelect: true,
		  	infinite: true,
		  	dots: false,
  			touchThreshold: 15,
			speed: 200,
			variableWidth: false,
			}
		},
		{
		breakpoint: 320,
		settings: {
	        slidesToScroll: 1,
	        slidesToShow: 1,
		  	centerMode: true,
		  	centerPadding: '15px',
		  	focusOnSelect: true,
		  	infinite: true,
		  	dots: false,
  			touchThreshold: 15,
			speed: 200,
			variableWidth: false,
			}
		}
		// You can unslick at a given breakpoint now by adding:
		// settings: "unslick"
		// instead of a settings object
		]
	});
});
$(function(){
$(document).on('click','.trip-stories-carousel .slick-list .slick-track .slick-slide',function(){
    let nextPageURL = $(this).children("div").children(".c-item").children("a").attr("href");
    let ctaText = $(this).children("div").children(".c-item").children("a").children(".card-comp-txt").children("h3").text().trim();
    let title = $(this).children("div").children(".c-item").children("a").children(".card-comp-txt").children("h3").text().trim();
    dataLayer.push({
        'event':'relatedTripStories',
        'eventCategory':'relatedTripStories',
        'eventAction':ctaText,
        'eventLabel':nextPageURL,
        'arrowClicked':"",
        'title':title  
    });
});

$(document).on('click','.trip-stories-carousel .slick-prev,.trip-stories-carousel .slick-next',function(){
    let arrowClicked = $(this).attr("aria-label");
    let nextPageURL = $(this).attr("href");
    if(nextPageURL == undefined){
        nextPageURL = "";
    }
    let ctaText = $(this).text().trim();
let title = $(this).siblings(".slick-list").children(".slick-track").children(".slick-active").children("div").children(".c-item").children("a").children(".card-comp-txt").children("h3").text().trim();
        dataLayer.push({
            'event':'relatedTripStories',
            'eventCategory':'relatedTripStories',
            'eventAction':ctaText,
            'eventLabel':nextPageURL,
            'arrowClicked':arrowClicked,
            'title': title
        });
});
});
